import {create} from 'zustand';
import produce from 'immer';
import { persist } from 'zustand/middleware';

const useRecipeStore = create(persist((set) => ({
    recipes: [persist.state],
    isLoading: false,
    isActiveRecipes: [],
    loadRecipes: async (page) => {
        set({ isLoading: true });
        try {
            const response = await fetch(`https://api.punkapi.com/v2/beers?page=${page}`);
            const data = await response.json();
            set(produce((state) => {
                state.recipes = data;
                state.isLoading = false;
            }));
        } catch (error) {
            console.error('Failed to load recipes:', error);
            set({ isLoading: false });
        }
    },
    deleteRecipe: (recipeId) => {
        set(produce((state) => {
            state.recipes = state.recipes.filter((recipe) => recipe.id !== recipeId);
            console.log(state.recipes)
    }));
    },
    isActiveRecipe: () => {
        set(produce((state) => {
            state.recipes = state.recipes.filter((recipe) => recipe.className !== 'active');
            state.isActiveRecipes.push(state.isActiveRecipe);
    }));
    },
}), {
    name: 'recipe-store',
}));

export default useRecipeStore;
