import useRecipeStore from '../../zustand/RecipesStore';
import { useEffect, useState } from 'react';
import RecipeCard from '../RecipeCard/RecipeCard';
import { RecipeListGallery } from './RecipeListStyled';
import Loader from '../loader/loader';

const RecipeList = () => {
  const { recipes, isLoading, loadRecipes } = useRecipeStore();
  const [page, setPage] = useState(1);
    const [selectedRecipeIds, setSelectedRecipeIds] = useState([]);


  useEffect(() => {
    loadRecipes(page);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  useEffect(() => {
    if (recipes.length === 0 || recipes === null || recipes === undefined) {
      let nextPage = page + 1;
      loadRecipes(nextPage);
      setPage(nextPage);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [recipes.length === 0, recipes === null]);


const toggleRecipeSelection = (recipeId) => {
    setSelectedRecipeIds((prevIds) => {
      if (prevIds.includes(recipeId)) {
        return prevIds.filter((id) => id !== recipeId);
      } else {
        return [...prevIds, recipeId];
      }
    });
  };

  const handleDeleteSelectedRecipes = () => {
    deleteSelectedRecipes(selectedRecipeIds);
    setSelectedRecipeIds([]);
  };


  if (isLoading) {
    return <div style={{display:'flex', justifyContent:'center', alignItems:'center'}}>
      <Loader />
    </div>
  }
  return (
    <>
       <div>
        <button onClick={handleDeleteSelectedRecipes}>Видалити обрані</button>
      </div>
        <RecipeListGallery >
          {recipes.slice(0, 15).map((recipe) => (
            <RecipeCard key={recipe.id} recipe={recipe}
        isSelected={selectedRecipeIds.includes(recipe.id)}
            toggleSelection={toggleRecipeSelection}    />
      ))}
        </RecipeListGallery>
        </>
  );
};

export default RecipeList;
