import useRecipeStore from '../../zustand/RecipesStore';
import { useEffect, useState } from 'react';
import RecipeCard from '../RecipeCard/RecipeCard';
import { RecipeListGallery } from './RecipeListStyled';
import Loader from '../loader/loader';

const RecipeList = () => {
  const [isActive, setIsActive] = useState(false);
  const { recipes, isLoading, loadRecipes } = useRecipeStore();
  const [page, setPage] = useState(1)

  useEffect(() => {
    loadRecipes(page);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  useEffect(() => {
    if (recipes.length === 0 || recipes === null || recipes === undefined) {
      let nextPage = page + 1;
      loadRecipes(nextPage);
      setPage(nextPage);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [recipes.length === 0, recipes === null]);

function toggleClass () {
    setIsActive(prevState => !prevState);
  };

 function handleClick(event) {
   if (event.target.nodeName === 'UL') {
     return;
   }

   if (event.button === 2) {
        event.preventDefault();
            toggleClass(event.target.offsetParent.id);
          // console.dir(event.target.offsetParent.id)
        }
    }

    const isActiveClass = isActive ? 'active' : ' ';


  if (isLoading) {
    return <div style={{display:'flex', justifyContent:'center', alignItems:'center'}}>
      <Loader />
    </div>
  }
  return (
    <>
        <RecipeListGallery onContextMenu={handleClick}>
          {recipes.slice(0, 15).map((recipe) => (
        <RecipeCard className={isActiveClass} key={recipe.id} recipe={recipe} />
      ))}
        </RecipeListGallery>
        </>
  );
};

export default RecipeList;
